package com.jty.model;import java.io.Serializable;import java.util.Date;import javax.persistence.Entity;import javax.persistence.Id;@SuppressWarnings("serial")@Entity(name = "announce")public class Announce implements Serializable {    @Id	private Integer id;    private String title;    private String content;    private Date createTime;    private Integer deleteFlag;	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public Date getCreateTime() {		return createTime;	}	public void setCreateTime(Date createTime) {		this.createTime = createTime;	}	public Integer getDeleteFlag() {		return deleteFlag;	}	public void setDeleteFlag(Integer deleteFlag) {		this.deleteFlag = deleteFlag;	}    }