package com.jty.model;import java.io.Serializable;import java.util.Date;import javax.persistence.Entity;import javax.persistence.Id;@SuppressWarnings("serial")@Entity(name = "balance")public class Balance implements Serializable {    @Id	private Long id;    private Long userId;    private Float balanceIntegral;    private Double balanceAmount;    private Date LastUpdateTime;	public Long getId() {		return id;	}	public void setId(Long id) {		this.id = id;	}	public Long getUserId() {		return userId;	}	public void setUserId(Long userId) {		this.userId = userId;	}	public Double getBalanceAmount() {		return balanceAmount;	}	public void setBalanceAmount(Double balanceAmount) {		this.balanceAmount = balanceAmount;	}	public Date getLastUpdateTime() {		return LastUpdateTime;	}	public void setLastUpdateTime(Date lastUpdateTime) {		LastUpdateTime = lastUpdateTime;	}	public Float getBalanceIntegral() {		return balanceIntegral;	}	public void setBalanceIntegral(Float balanceIntegral) {		this.balanceIntegral = balanceIntegral;	}	}