package com.jty.model;import java.io.Serializable;import java.util.Date;import javax.persistence.Entity;import javax.persistence.Id;@SuppressWarnings("serial")@Entity(name = "buyorderdetail")public class OrderDetail implements Serializable {    @Id	private Integer id;    private String orderCode;    private Long userId;    private String productid;    private Double productMoney;    private Date createTime;    private Integer orderState;	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getOrderCode() {		return orderCode;	}	public void setOrderCode(String orderCode) {		this.orderCode = orderCode;	}	public Long getUserId() {		return userId;	}	public void setUserId(Long userId) {		this.userId = userId;	}	public String getProductid() {		return productid;	}	public void setProductid(String productid) {		this.productid = productid;	}	public Double getProductMoney() {		return productMoney;	}	public void setProductMoney(Double productMoney) {		this.productMoney = productMoney;	}	public Date getCreateTime() {		return createTime;	}	public void setCreateTime(Date createTime) {		this.createTime = createTime;	}	public Integer getOrderState() {		return orderState;	}	public void setOrderState(Integer orderState) {		this.orderState = orderState;	}	}