package com.jty.model;import java.io.Serializable;import java.util.Date;import javax.persistence.Entity;import javax.persistence.Id;@SuppressWarnings("serial")@Entity(name = "producttype")public class ProductType implements Serializable {    @Id	private Integer id;    private String productNo;    private String productName;    private Double money;    private Float rate;    private Float price;    private Integer isRate;        	public Float getPrice() {		return price;	}	public void setPrice(Float price) {		this.price = price;	}	public Integer getIsRate() {		return isRate;	}	public void setIsRate(Integer isRate) {		this.isRate = isRate;	}	public Float getRate() {		return rate;	}	public void setRate(Float rate) {		this.rate = rate;	}	public Integer getId() {		return id;	}	public void setId(Integer id) {		this.id = id;	}	public String getProductNo() {		return productNo;	}	public void setProductNo(String productNo) {		this.productNo = productNo;	}	public String getProductName() {		return productName;	}	public void setProductName(String productName) {		this.productName = productName;	}	public Double getMoney() {		return money;	}	public void setMoney(Double money) {		this.money = money;	}}